mapper.select:
		select t.*,s.store_now from ykj_storage_goods t left join ykj_storage_goods_status s on s.id = t.id
		
			
                and name like CONCAT('%',CONCAT(#{fuzzyName},'%'))
            
		    
                and model like CONCAT('%',CONCAT(#{fuzzyModel},'%'))
            
            
                and business_id = #{businessId}
            
            
                and onsale_status = #{onsaleStatus}

		select * from ykj_storage_goods 
		
		    
                and name like '%${@com.gnet.utils.string.StringEscapeUtils@escapeSql(name)}%'
            
            
                and onsale_status = #{onsaleStatus}

select * from ykj_storage_goods 

	
		and name = #{name}
	
	
		and onsale_status = #{onsaleStatus}
	



