mapper.select:
select t.*,(select s.value from sc_codeword s where s.code=t.category and s.codeword_type_id = '17') category_name,
(select s.value from sc_codeword s where s.code=t.pay_way and s.codeword_type_id = '3') pay_way_name 
 from ykj_revenue_and_refund t where t.id = #{id}

		select t.*,q.name recorderName,
		(select s.value from sc_codeword s,sc_codeword_type w where w.id=s.codeword_type_id and s.code=t.category and w.type_value = 'CATEGORY') category_name,
		(select s.value from sc_codeword s,sc_codeword_type w where w.id=s.codeword_type_id and s.code=t.pay_way and w.type_value = 'PAYMENT_WAY') pay_way_name 
		 from ykj_revenue_and_refund t left join ykj_clerk q on q.id = t.recorder
		
			
                and order_id = #{orderId}
            
		    
                and category  = #{category}
            
            
                and pay_way = #{payWay}

select sum(case t.category when ${@com.gnet.app.revenueandrefund.RevenueAndRefund@TYPE_CATEGORY_REFUND} then -payment else payment end) amount 
from ykj_revenue_and_refund t 
where t.order_id = #{orderId}
